// 封装加载图片的promise  
    let loadImg = (src) => {
        let img = new Image()
        img.src = src
        return new Promise((resolve, reject) => {
            // write your code here
            img.onload = () => {
                resolve(img)
            }
            img.onerror = (e) => {
                reject(e)
            }
        })
    }
    const imgs = ['https://ps.ssl.qhimg.com/sdmt/179_135_100/t0100714605ba9621e2.jpg', 'https://ps.ssl.qhimg.com/sdmt/179_135_100/t0100714605ba9621e2.jpg', 'https://ps.ssl.qhimg.com/sdmt/179_135_100/t0100714605ba9621e2.jpg'];
    // 依次加载图片
    async function fSync(imgs) {
        // write your code here
        imgs.forEach(item => {
            loadImg(item).then((img) => {
                document.body.appendChild(img)
            })
        });
    }

