student_answer:
let loadImg = (src) => {
  return new Promise((resolve, reject) => {
    // write your code here
    let img = document.createElement('img');
    img.src = src;
    document.body.append(img);
    setTimeout(() => {
      resolve(true);
    }, 1500);
  });
}
const imgs = ["img/img1.jpg", "img/img2.jpg", "img/img3.jpg"];
// 依次加载图片
async function fSync(imgs) {
  // write your code here
  for (let i of imgs) {
    await loadImg(i);
  }
}
fSync(imgs);

