            var U = 0, D = 0, L = 0, R = 0;
            for(var i=0; i < str.length; i++){
                switch (str[i]){
                    case "U":______ ;break;
                    case "D":______ ;break;
                    case "L":______ ;break;
                    case "R":______ ;break;
                }
            }
            return ( ______ && ______ ) 
  }

