script里面:
第一条：
//使用闭包
var ul = document.getElementsByTagName("ul")[0];
var list = ul.getElementsByTagName("li");
	function foo(){
		for(var i = 0, len = list.length; i < len; i++){
			var that = list[i];
			list[i].onclick = (function(k){
				var info = that.innerHTML;
				return function(){
					alert(k + "----" + info);
				};
			})(i);
		}
	}
	foo();

