//使用ES6中的新特性let来声明变量
var ul = document.getElementsByTagName("ul")[0];
var list = ul.getElementsByTagName("li");
	function foo(){
		for(let i = 0, len = list.length; i < len; i++){
			list[i].onclick = function(){
				alert(i + "----" + this.innerHTML);
			}
		}
	}
foo();

