function loadImg(url) {
  return new Promise((resolve, reject) => {
    const img = new Image();
    img.onload = function() {
      resolve(img);
    }
    img.onerror = function() {
      img.src = "./img/error.jpg"
      reject(img);
    }
    img.src = url;
  })
}
var arr = [
  "./img/1.jpg",
  "./img/4.jpg",
  "./img/3.jpg"
]
var imgs = arr.map(item => {
  loadImg(item).then(Img => {
    document.getElementsByTagName("body")[0].appendChild(Img)
  }).catch(errImg => {
    document.getElementsByTagName("body")[0].appendChild(errImg)
  })
})
