const fs = require('fs');
class readFile {
    constructor(url) {
        this.url = url;
        this.init()
    }
    init() {
        let size = fs.statSync(this.url).size;//b 1kb=1024b
        console.log(size)
        if (size > 2000) {
            // 默认为大文件
            this.readStream()
        } else {
            // 默认为小文件
            this.readSmallFile()
        }
    }
    readStream() {
        let rs = fs.createReadStream(this.url);// 可读流
        let ws = fs.createWriteStream('./big/' + this.url);
        rs.pipe(ws)
    }
    readSmallFile() {
        fs.readFile(this.url, 'utf-8', (err, con) => {
            if (err) throw err;
            fs.writeFileSync('./small/' + this.url, con)
        })
    }
}
