const path = require("path");
const fs = require("fs");

function copybig(orgin, target) {
    let rs = fs.createReadStream(orgin);
    let ws = fs.createWriteStream(target);
    rs.pipe(ws)
}

function copysmall(orgin, target) {
    fs.readFile(orgin, (err, data) => {
        if (!err) {
            fs.writeFileSync(target, data)
        }
    })
}

function copy(orgin, target, size = 17000) {
    fs.stat(orgin, (err, data) => {
        if (!err) {
            if (data.size > size) {
                copysmall(orgin, target)
            } else {
                copybig(orgin, target)
            }
        }
    })
}

function copyDir(orgin, dir) {
    if (!fs.existsSync(dir)) {
        fs.mkdirSync(dir)
    }
    let arr = fs.readdirSync(orgin);
    arr.forEach(item => {
        if (fs.statSync(path.join(orgin, item)).isFile()) {
            copy(path.join(orgin, item), path.join(dir, item))
        } else {
            copyDir(path.join(orgin, item), path.join(dir, item))
        }
    })
}
