let oUl = document.getElementsByTagName('ul')[0];
       let li = document.getElementsByTagName('li');
       const {
           map,
           forEach
       } = [];
       //map forEach为高阶函数 可遍历 map有返回值为一个数值 forEach没有返回值

       //遍历每个li 为每个li重新赋key值并返回li的数组集合
       li = map.call(li, item => {
           //item为每个li
           //key为每个li自定义属性key值
           let key = 2 * item.getAttribute('key') - 1;
           //为每个li的key重新赋值
           item.setAttribute('key', key);
           //改变每个li自定义属性key值后返回 
           return item;
       });

       //ul的内容清空
       oUl.innerHTML = '';
       //遍历map返回的li数值  并追加到ul里面
       forEach.call(li, item => {
           oUl.appendChild(item);
       })

