import router from './router'
import { getToken } from '@/utils/auth' // getToken from cookie 从cookie获取令牌
import Login from '@/utils/login'
import Register from '@/utils/register'
import AuthRedirect from '@/utils/auth-redirect'

const whiteList = ['/login', '/register', '/auth-redirect']// no redirect whitelist  没有重定向白名单
const router=new Router({
    routes:[
        {
            path:'/login',
            name:'login',
            compoent:Login
        },
        {
            path:'/register',
            name:'register',
            compoent:Register
        },
        {
            path:'/auth-redirect',
            name:'AuthRedirect',
            compoent:AuthRedirect
        }
    ]
})


router.beforeEach((to, from, next) => {
    // to: Route: 即将要进入的目标 路由对象
    // from: Route: 当前导航正要离开的路由
    // next: Function: 一定要调用该方法来 resolve 这个钩子。执行效果依赖 next 方法的调用参数。

    if (getToken()) { // determine if there has token 确定是否有令牌
        // write your code here
        if(whiteList.indexOf(to.name)!==-1){
            router.push({name:to.name})
        }

    } else {
        // write your code here


    }
})
