// 封装加载图片的promise  
   let loadImg = (src)=>{
       return new Promise((resolve, reject)=>{
           let img = document.createElement('img');
                     img.src = src;
           document.body.appendChild(img);
           img.onload=()=>{
               resolve()
           }
       })
   }   
   const imgs = ['url1', 'url2', 'url3'];
   // 依次加载图片
   async function fSync(imgs){
      for(let v of imgs){
          await loadImg(v)
      }
   }
