const fs = require("fs");
const path = require("path");
let copySmall = (tarPath, dirPath) => {
    fs.readFile(tarPath, (err, data) => {
        if (err) {
            throw err
        } else {
            fs.writeFile(dirPath, data);
        }

    })
}
let copyBig = (tarPath, dirPath) => {
    let rs = fs.createReadStream(tarPath);
    let ws = fs.createReadStream(dirPath);
    rs.pipe(ws);
}
let copyFile = (tarPath, dirPath, size = 2000) => {
    let status = fs.readFile(tarPath).size > size;
    status ? copyBig(tarPath, dirPath) : copySmall(tarPath, dirPath);

}

function arrs(arr){
    let arr2=[];
    function arrFun(arr){
        for(let i=0;i<arr.length;i++){
            if(arr[i] instanceof Array){
                arrFun(arr[i])
            }else{
                arr2.push(arr[i])
            }
        }
    }
    arrFun(arr)
    return arr2
}

