 const ipt = document.getElementById('input')

 function debounce(fun,delay){
    let timer ;
    return function(args){
        const that = this
        clearTimeout(timer)
        timer = setTimeout(function(){
             fun.call(that,args)
        },delay)
    }
    }
 const debounceInput = debounce(input,300)
ipt.addEventListener("change",function(e){
    debounceInput(e.target.value)
 })
