class Watch {
			constructor(){
				this.handle=[]
			}
			on(type,fun) {
				// 实现该方法逻辑
			   this.handle.push({type:type,fun:fun});
			   return this;
			}
			emit(type,val) {
				// 实现该方法逻辑 
				 this.handle.forEach((item,index)=>{
					  if(item.type===type){
						  item.fun(val)
					  }
				 })
			}
		}
		const w1 = new Watch();
		w1.on('www', (options) => {
			console.log(options)
		});
		w1.emit('www', 'success');

