computedPrice = (car) => {
			let carObj = {};
			carObj.group = [];
			carObj.allPrice=0;
			car.forEach((item, index) => {
				let obj = {}
				obj.typeName = item.groupName;
				let num = 0;
				item.type.forEach((it, ind) => {
					num += it.price;
				})
				carObj.allPrice+=num;
				obj.price = num;
				carObj.group.push(obj);
			})
			return carObj;
		}
