class Watch {
  	constructor() {
    this.events = {};
	}
	on(type, obj) {
		this.events[type] instanceof Array
		? this.events[type].push(obj)
		: (this.events[type] = [obj]);
 	}
	emit(type,value) {
		this.events[type].forEach(item => item(value))
    }
};

