let end = null;
(function counter(arr) {
  let allPrice = 0;
  let group = [];
  arr.forEach(ele => {
    let price = ele.type.reduce(function(prev, cur) {
      allPrice += cur.price;
      return cur.price + prev;
    }, 0)
    group.push({
      typeName: ele.groupName,
      price
    })
  });
