function small(ori,tar){
    fs.readFileSync(ori)
    fs.writeFileSync(tar)
}
function big(ori,tar){
    let rs=fs.createReadStream(ori)
    let ws=fs.createWriteStream(tar)
    rs.pipe(ws)
}
function copy(ori,tar,size=20000){
    fs.stat(ori,(err,data)=>{
        if(!err){
            data.size>size?big(ori,tar):small(ori,tar)
        }
    })
}
