let loadImg = (src) => {
        return new Promise((resolve, reject) => {
            let img = document.createElement('img')
            img.src = src
            document.body.append(img)
            setTimeout(() => {
                resolve(true)
            }, 1000)
        })
    }
    const imgs = ['../src/img/1.PNG', '../src/img/1.PNG', '../src/img/1.PNG'];
    //const imgs = ['url1', 'url2', 'url3'];
    // 依次加载图片
    async function fSync(imgs) {
        for (let i of imgs) {
            await loadImg(i)
        }
    }
