let number=0;//记录次数
function jump(num,res){//定义jump函数
	if(num===0){//第一次执行创建新数组
    	res=[]//用来放相加等于1和2步
    }
  	var i=1;//定义一个while循环条件变量
  	while(i<3){	//循环2次，取1或2
		if(num+i<=10){	//步数总和是否大于10
        	let _res=[...res]	//引用值类型解构赋值，创建新的引用值的地址；
            _res.push(i);	//把当前循环的i放入步数数组
          	if(num+i===10){  //如果当前的num加上i等于10，打印出当前的number次数和步数数组
            	number++;	//记录次数增加
              	console.log(number,_res)//打印出这次的结果
            }else{
            	jump(num+i,_res); //num+i不够10，从新调用自身传入下次while内判断用的num和当前所有步数相加不够10的数组；
            }
        }
      i++//判断while第3次终止
    }
}
jump(0)//调用jump方法并传入初始值0

let fs=require("fs");	//获取操作文件
let TargetPath="要读取文件的位置";

function read(TargetPath) {
    let size=fs.statSync(TargetPath).size/1024/1024;		//获取文件大小
    if (size>2){	//判断是否大于2M，大于用流，小于用块
        let read=fs.createReadStream(TargetPath),write=fs.createWriteStream("要把文件放入的位置");//创建流式读取写入
        read.pipe(write);//管道方法传输
    }else{
        let read=fs.readFileSync(TargetPath);//读取文件
        fs.writeFileSync("要把文件放入的位置",read)//放入文件
    }
}
read(TargetPath);//调用并传入要读取文件的地址
