class Watch{
  constructor() {
     this.arr = []
  }
  on(key, fn){
  		if (!this.arr[key]) {
        this.arr[key] = [];
       }
    	this.arr[key].push(fn);
  }
  emit(){
   		var key = Array.prototype.shift.call(arguments), fns = this.arr[key];
  		if (!fns || fns.length === 0) {
        	return false;
        }
      for (var i = 0, fn; fn = fns[i++];) {
			fn.apply(this, arguments);
      }
  }
}

