function convert(list, parentKey, currentKey, rootValue) {
  let tree = {};
  let arr=[];
  tree.children=[];

  list.forEach(v=>{
    if(v[parentKey]===rootValue){
      tree.id=rootValue;
      tree.children.push(v);
    }else{
      arr.push(v);
    }
  });
  return tree;
}

function getRate(starNum,num){
  var star=[];
  if(!num){
    num=5;
  }
  if( starNum>num || starNum<=0 || num<0 || num.toString().length>1){
    return "error"
  }else{
    for(i=0;i<starNum;i++){
      star.push('★')
    }
    for(i=0;i<num-starNum;i++){
      star.push('☆')
    }
  }
  return star.join('')
}
