var fs = require('fs');
fs.readdir('./src', function (err, paths) { //找到集合
    if (err) {
        throw err;
    };
    paths.forEach(function (file) { //遍历paths
        //拼接路径
        var pathname = __dirname + '/src/' + file;
        fs.stat(pathname, function (err, st) { //查看文件
            if (err) {
                throw err;
            };
            var size = st.size / 1024 / 1024;
            if (size <= 2) { //判断大小
                create('./big', function () { //创建大文件夹
                    var rs = fs.createReadStream(pathname);
                    var ws = fs.createWriteStream('./big/' + file);
                    rs.pipe(ws);
                });
            }
        });
    })
})

function create(dist, ck) { //调用函数
    if (!fs.existsSync(dist)) { //判断文件夹是否存在
        fs.mkdirSync(dist);
        ck && ck();
    } 
};

function arrToOne($multi) {
$res = array();
foreach ($multi as $key => $val) {
if( is_array($val) ) {
$res = array_merge($res, arrToOne($val));
} else {
$res[] = $val;
}
}
return $res;
}

function halfStar(x) {
    var star = document.getElementById('star');
    var items = star.getElementsByTagName("li");
    x = parseInt(x) - 1;
    var tem = -1;
    for(var i = 0; i < items.length; i++) {
        if(x > i*2) {
            items[i].style.background = "url('../img/star/full.png') no-repeat";
            items[i].style.backgroundSize = "100% 100%";
        } else {
            if(tem == -1) {
                tem = i;
            } 
            items[i].style.background = "url('../img/star/empty.png') no-repeat";
            items[i].style.backgroundSize = "100% 100%";
        }
    }
    if(x == parseInt(tem)*2) {
        items[tem].style.background = "url('../img/star/half.png') no-repeat";
        items[tem].style.backgroundSize = "100% 100%";
    }
    document.getElementById('getgrade').innerHTML = parseInt(x)+1+'.0';
}
