class PriorityQueue {
        constuctor() {
            this.arr = [];
        }
        enqueue(str, num) {
            let arr = this.arr ? this.arr : [];
            if (arr.length > 0) {
                let bool = false;
                arr.forEach((item, i) => {
                    if (num == item[0]) {
                        item.push({
                            str: str,
                            num: num
                        })
                        bool = true;
                    }
                })
                if (!bool) {
                    arr.forEach((item, i) => {
                        if (num < item[0]) {
                            arr.splice(i, 0, [num, {
                                str: str,
                                num: num
                            }])
                        } else {
                            if (i === arr.length - 1) {
                                arr.push([num, {
                                    str: str,
                                    num: num
                                }])
                            }
                        }
                    })

                }
            } else {
                arr.push([num, {
                    str: str,
                    num: num
                }])
            }
            this.arr = arr;
        }
