public int JumpFloor(int target) {
        int x=1, y=2, z;
        if(target == 1){
            return 1;
        }
        if(target == 2){
            return 2;
        }
        for(int i = 3; i <= target; i ++){
            z = y;
            y = x + y;
            x = z;
        }
        return y;

