            delete item.classroomId
            return item
        })
    })
    await fetchData("/api/courses").then(data=>{
        Response.forEach(item=>{
            let scores = data.map(i => {
                if (i.studentId === item.id) {
                    return i.score ? i.score : 90
                }
            })
            item.average = scores.reduce((prev, next, item, arr) => {
                return prev + next
            })/scores.length
        });
    })
    return Response
}
