let loadImg = (src)=>{
    return new Promise((resolve, reject)=>{
        // write your code here
      let img = document.createELement("img");
      img.src = src;
      img.onload = function(){
        resolve(img)
      }
      img.onerror = function(err){
      	reject(err)
      }
    })
}
