let oUl = document.getElementsByTagName('ul')[0]; //获取到ul标签
let li = document.getElementsByTagName('li'); // 获取到所有的li标签
const {map,forEach} = [];	//结构数组
li = map.call(li,item=>{   //将map的指针指向改变
  let key = 2 * item.getAttribute('key') - 1; 
  item.setAttribute('key',key); //改变li的key的值 （将key的值都乘以二了）
  return item; 
});
oUl.innerHTML = ''; //清空了ul的内容
forEach.call(li,item=>{ //改变forEach的指向
  oUl.appendChild(item); //将改变过得li重新放到ul内
})

