const fs = require('fs');
class readFile {
    constructor(url) {
        this.url = url;
        this.init();
    }
    init() {
        let size = fs.statSync(this.url).size;
        if (size > 2000) {
            this.dawenjian();
        } else {
            this.xiaowenjian();
        }
    }
    dawenjian() {
        let rs = fs.createReadStream(this.url);
        let ws = fs.createWriteStream('./din/' + this.url);
        rs.pipe(ws)
    }
    xiaowenjian() {
        fs.readFile(this.url, 'utf-8', (err, con) => {
            if (err) throw err;
            fs.writeFileSync('./small/' + this.url, con)
        })
    }
  new readFile(url)
}

function getRate(num, sum) {
    switch (true) {
        case num > sum:
        case num < 0:
        case Number.isInteger(sum) == false && !!sum:
            return "error"
            break;
        default:
            return creat(num, sum)
            break;
    }
}
function creat(num, sum = 5) {
    let arr = [];
    for (let i = 0; i < sum; i++) {
        if (i < Math.round(num)) {
            arr.push("★")
        } else {
            arr.push("☆")
        }
    }
    return arr
} 
