class Solution {
public:
    int jumpFloorII(int number) {
        int result=0;
        if(number==0)
        {
          result=0;
        }
        else if(number==1)
        {
            result=1;
        }
        else
        {
            result=2*jumpFloorII(number-1);
        }
        return result; 
    }
};

const { mkdirSync, readdirSync, statSync, createReadStream, createWriteStream, readFileSync, writeFileSync } = require("fs");
const { join } = require("path");
function copy({ source, target }) {
    mkdirSync(target);
    readdirSync(source).forEach(item => {
        let sourcePath = join(source, item);
        let targetPath = join(target, item);
        let isFile = statSync(sourcePath).isFile();

        if (isFile) {
            if (statSync(sourcePath).size > 10 * 1024 * 1024) {
                let rs = createReadStream(sourcePath);
                let ws = createWriteStream(targetPath);
                rs.pipe(ws);
            } else {
                let con = readFileSync(sourcePath);
                writeFileSync(targetPath, con);
            }
        } else {
            copy({
                source: sourcePath,
                target: targetPath
            })
        }
    })
}
