function normalize(arr){
   if(!arr){
       alert('the arr is empty!');
       return;
    }
    return arr.map(function(s){
      var temparr = [];
      for(var j=0; j<s.length; j++){
           var str = s.substr(j, 1);
           if(j===0){
              str = str.toUpperCase();
           }else{
              str = str.toLowerCase();
           }
           temparr.push(str);
      }
      s = temparr.reduce(function(x, y){
           return x + y;
      });
      return s;
    });

}
