let loadImg = (src) => {
    return new Promise((resolve, reject) => {
        let img = new Image();
        console.log(src);
        img.src = src;
        document.body.appendChild(img);
        img.onload = () => {
            setTimeout(() => {
                resolve();
            }, 1000)
        }
    })
}


const imgs = ['./img/one.jpg', './img/two.jpg', './img/three.jpg', './img/four.png', './img/five.jpg'];


async function FSync(imgs) {
    for (let i of imgs) {
        await loadImg(i);
    }
} 
FSync(imgs);
