function computedPrice(car) {
let obj = {
allPrice: null,
group: [{
typeName: '水果',
price: null
},
{
typeName: '蔬菜',
price: null
}
]
}
let asd = car[0].type.reduce((s, v) => {
return s + v.price
}, 0)
let add = car[1].type.reduce((s, v) => {
return s + v.price
}, 0)
console.log(add)
obj.allPrice = asd + add
obj.group[0].price = asd
obj.group[1].price = add
return obj;
}

