class Solution { 
  public: 
    bool hasPathSum(TreeNode* root, int sum) { 
      if(!root) 
        return false; 
      else if(!root->left&&!root->right) 
        return sum == root->val; 
      else if(root->left&&!root->right) 
      { 
        sum -= root->val; 
        return hasPathSum(root->left,sum); 
      } 
      else if(!root->left&&root->right) 
      { 
        sum -= root->val; 
        return hasPathSum(root->right,sum); 
      } 
      else 
      { 
        sum -= root->val; 
        return hasPathSum(root->left,sum)||hasPathSum(root->right,sum); 
      } 
    } 
};
