function img(imgUrl){
    return new Promise((resolve,reject)=>{
        resolve(imgUrl)
    })
}
var imgs = ["http://www.pptbz.com/pptpic/UploadFiles_6909/201203/2012031220134655.jpg","http://pic3.nipic.com/20090527/1242397_102231006_2.jpg","12.jpg","24.jpg","25.jpg","http://pic3.nipic.com/20090527/1242397_102231006_2.jpg"]
imgs.forEach((item)=>{
    img(item).then((res)=>{
        let url = document.createElement("img");
        url.src = res;
        document.body.appendChild(url)
    })
})
