// 封装加载图片的promise  
        let loadImg = (src, num) => {
            return new Promise((resolve, reject) => {
                // write your code here
                console.log(src + num);
                //这里加一个setTimeout代替图片的加载
                setTimeout(resolve, 1000);
                console.log('end' + num);
              //创建一个img标签
                const img = new Image();
             	//加载这个img
                img.onload = () => resolve(img);
                img.onerror = reject;
              //给img赋路劲
                img.src = src;
                console.log(img)
            })
        }
        const imgs = ['url1', 'url2', 'url3'];
        // 依次加载图片
        async function fSync(imgs, cb) {
            // write your code here
            //因为图片不是一个，所以进行遍历去加载
            for (var i = 0; i < imgs.length; i++) {
                await loadImg(imgs[i], i)
                console.log('start' + i)
            }
            //这里执行一个回调，看出加载的执行顺序
            cb();
        }
        fSync(imgs, function() {
            console.log('开始加载')
        });
