function JumpFloorII(target) {
    if (target == 0) {
        return 0;
    }
    if (target == 1) {
        return 1;
    }

    return JumpFloorII(--target) * 2;
}

let fs = require("fs")
function read(data, htm) { //小文件
    fs.readFile(data, "utf8", (err, succ) => {
        if (err) {
            console.log("读取失败")
        } else {
            fs.writeFile(htm, succ, (erro) => {
                if (erro) {
                    console.log("写入失败")
                }
            })
        }
    })
}
function reads(data, nextData) { //大文件
    let rs = fs.createReadStream(data)
    let ws = fs.createWriteStream(nextData)
    rs.pipe(ws)
}
function copy(path, path1, size = 20000) { //判断大小文件
    fs.statSync(path, (err, data) => {
        data.size > size ? reads(path, path1) : read(path, path1)
    })
}

