function getRate(start, end) {
    if (start < 0 || end < 0) {
        return 'error'
    } else {
        if (arguments.length === 1) {
            if (start > 5) {
                return 'error'
            } else {
                let arr = []
                for (var i = 0; i < start; i++) {
                    arr.push('★')
                }
                for (var l = 0; l < 5 - start; l++) {
                    arr.push('☆')
                }
                return arr
            }
        } else if (arguments.length === 2) {
            let arr = []
            for (var i = 0; i < start; i++) {
                arr.push('★')
            }
            for (var l = 0; l < end - start; l++) {
                arr.push('☆')
            }
            return arr
        }
    }
}
