    }


    // 返回所有成员变量
    config() {
        return this;
    }


    // 获得URL
    buy() {
        // 设置返回URL的默认值
        var url = "https://www.aliyun.com/buy?";
        // 设置一个空数组，用于存放参数
        var urlArr = [];
        for (let key in this) {
            // 判断是否存在某属性，这里也可以用this.hasOwnProperty(key)
            if (this[key])
                urlArr.push(key + "=" + this[key]);// 拼接后放到数组中
        }
        // 将urlArr以"&"分割转为字符串
        url += urlArr.join("&");
        return url;
    }
}


