// 封装加载图片的promise  
let loadImg = (src) => {
  return new Promise((resolve, reject) => {
    // write your code here
    let img = new Image;
    img.src = './img/' + src;
    let box = document.querySelector('.box');
    box.appendChild(img);
    setTimeout(() => {
      resolve(true);
    }, 200)
  })
}
const imgs = ['(22).jpg', '(23).jpg', '[feiq]4876.jpg'];
// 依次加载图片
async function fSync(imgs) {
  // write your code here
  for (let i of imgs) {
    await loadImg(i)
  }
}

fSync(imgs);
