const fs = require("fs");

function readFile(oldFile,newFile) {
    if (fs.statSync(oldFile).size >= 1024 * 1024 * 2) {
        let rs = fs.createReadStream(oldFile);
        let ws = fs.createWriteStream(newFile);
        rs.pipe(ws)
    } else {
        let data = fs.readFileSync(oldFile)
        fs.writeFileSync(newFile, data)
    }

}

