let oUl = document.getElementsByTagName('ul')[0];//获取第一个ul
    let li = document.getElementsByTagName('li');//获取所有的li
    const {map,forEach} = [];//定义一个空数组 用的解构
    li = map.call(li,item=>{  //遍历所有li
        let key = 2 * item.getAttribute('key') - 1;  //计算每一个li的key值*2-1
        item.setAttribute('key',key); // 给每一个li重新设置key值
        return item; //最终把li返回出去
    });
    oUl.innerHTML = ''; //先给ul的内容清空
    forEach.call(li,item=>{
        oUl.appendChild(item);//将每一个li追加到ul里
    })

