function Fibonacci(n) {
    if(n == 0) {
        return 0;
    }
    if(n == 1) {
        return 1;
    }
    if(n == 2) {
        return 2;
    }
    var minusOne = 1;
    var minusTwo = 2;
    var num;
    for(var i = 3; i <= n; i++) {
        num = minusOne + minusTwo;
        minusOne = minusTwo;
        minusTwo = num;
    }
    return num;
}
console.log(Fibonacci(10));

