let ArrayFn = (arr) => {
    let sec = [];

    let steamroller = (arr, sec) => {
        arr.forEach(item => {
            if (item instanceof Array) {
                steamroller(item, sec);
            } else {
                sec.push(item);
            }
        })
    }

    steamroller(arr, sec);
    return sec;
}

