  function computedPrice(arr) {
    let obj = {}
    let crr = []
    let drr = []
    let srr = []
    arr.forEach((item,index) => {
        item.type.forEach(function(ite,ind){
            crr.push(ite.price)
        })

         srr = item.type.map(function(ite,ind){ 
            return ite.price
        })

        drr.push({
            typeName : item.groupName,
            price : srr.reduce((prev,cur) => {
                return prev + cur
            })
        })

    })

    let allPrice = crr.reduce((prev,cur) => {
        return prev + cur
    })

    obj.allPrice = allPrice
    obj.group = drr
    return obj
}
