function getC($n,$start=1){
    $num = 1;
    for(;$start<=$n;$start++){
        $num *= $start;
    }
    return $num;
}
function getJump($x,$y){
    //echo $x.":".$y;
    if($x<=0 || $y <= 0){
        if($x <= 0 && $y <= 0){
            return 0;
        }
        return 1;
    }
    $n = $x +$y;
    $num = getC($n,$n-$y+1)/getC($y);
    return $num;
}
function jumpType($n){
    $count = 0;
    for($x=0;$x<=$n;){
        if(($n - $x)%2 == 0){
            $y = ($n - $x)/2;
            $num = getJump($x,$y);
            //echo "=".$num."<br/>";
            $count += $num;
            $x = $x + 2;
        }else{
            $x = $x + 1;
        }
    }
    return $count;
}
