var fs = require('fs');
var path = require('path')
function copy(src, dist, size = 100) {
    let sizeFile = fs.statSync(src).size;
    if (sizeFile < 100) {
        copySmall(src, dist)
    } else {
        copyBig(src, dist)
    }
}

