function splitArray(aim , size)
{    
    var result = [];
    var tempArray;
    for(var count = 0; count < aim.length; count++) 
    {  
        if(count%size == 0 )
        {
            if(tempArray)
            {
                result.push(tempArray);
            }
            tempArray = [];           
        }

        tempArray[tempArray.length]=aim[count];
        if(count+1 == aim.length)       
        {
            result.push(tempArray);
        }
    }
    return result;
}
