function jump(num,arr){
  //判断如果跳0次，arr为空数组
	if(num === 0){
    arr = [];
    }
  var i = 1;//定义i为1，while遍历i<3,每次跳一或二；
 while(i<3){
   //每一次相加都小于等于10
  	if(num+i<=10){
    let str = [...arr];//定义变量等于数组展开
     str.push(i);//把每次跳的放入里面
     if(num+i === 10){
       number++;
       console.log(num,str);//打印跳的次数，和跳几个台阶
     }else{
       jump(num + i,str)
     }
    }
    i++
  }
}
