var ul = document.querySelector("ul");
ul.onclick = function (e) {
  e = e || window.event;
  var target = e.target || e.srcElement;
  if (target.tagName === "LI") {
    var li = this.querySelectorAll("li");
    index = Array.prototype.indexOf.call(li, target);
    console.log(index)
  }
}
